import React, { useEffect, useContext, useState } from 'react';
import AnimeList from '../components/AnimeList';
import { SearchContext } from '../context/search'
import { Box, Typography, Grid, Paper } from '@material-ui/core'
import './Results.css'

const Results = (props) => {
  const search = useContext(SearchContext)
  const [dataExists, setDataExists] = useState(true)
  const Score = props.anime.score;

  useEffect(() => {
    if (search.animeData === undefined || search.animeData.length === 0) {
      try {
        search.setData(JSON.parse(localStorage.getItem('myData')));
        setDataExists(true);
      } catch (error) {
        console.log(error);
        setDataExists(false);
      }
    }
  }, [search]);
  return (
    <Box mt={2}>
      <Grid container item alignContent="center" alignItems="center" justify="center">
        <Paper item>
          <Typography className="results__average">
            Average score of all the animeâ€™s seasons: <strong>{sCORE}</strong>
          </Typography>
        </Paper>
      </Grid>
      {(dataExists && <AnimeList data={search.animeData}/>) || <Typography variant="h4">Data does not exist</Typography>}
    </Box>
  );
};

export default Results;
