import React, { useEffect } from 'react';
import { Grid, Typography, Paper, Link } from '@material-ui/core';

const SingleAnime = (props) => {
  const { 
    title, 
    image_url,
    episodes,
    rating, 
    airing, 
    broadcast, 
    score, 
    url} = props.info
    useEffect(() => {
      console.log(title, image_url)
    }, [title, image_url])
  return (
    <Grid container 
    spacing={10} 
    direction="row" 
    justify="center" 
    alignItems="center" 
    alignContent="center">
      <Grid item>
        <img src={image_url} alt={title} className="singleAnime__image" />
      </Grid>
      <Grid>
        <Paper elevation={3}>
          <Typography variant="h4" component="h2">
            {title}
          </Typography>
          <Typography variant="h5" component="h2">
            Airing: {String(airing)}
          </Typography>
          <Typography variant="h5" component="h2">
            Score: {score}
          </Typography>
          <Typography variant="h5" component="h2">
            Broadcast: {broadcast}
          </Typography>
          <Typography variant="h5" component="h2">
            Rating: {rating}
          </Typography>
          <Typography variant="h5" component="h2">
            Episodes: {episodes}
          </Typography>
          <Link component="button" vatiant="body1" href={url}>
            My Anime List
          </Link>
        </Paper>
      </Grid>
    </Grid>
  );
};

export default SingleAnime;
